<js>
JSON.parse(result).slice(0,10).map(o => {
    var J = org.jsoup.Jsoup.parse(java.ajax(o.read_url));
    return {
        name: o.name,
        author: o.author,
        tags: o.type + ','+ o.status,
        num: String(J.select('p.type').text()).replace(/\s*\|\s*.*/,'').replace('字数：',''),
