var tag3=result.match(/完成字(?:数|數):(\d*)/);
var intro=result.match(/works-intro-short.+?>([\s\S]*?)<\/div>/);
var last=result.match(/works-ft-new[\s\S]*?>([^<]*)<\/a/);
var author=result.match(/作者：([^<]*)<\/p>/);

var tags=((tag1) ? tag1[1]:'')+','+((tag2) ? tag2[1]:'')+','+((tag3) ? tag3[1]+'字':'');
intro=(intro) ? intro[1]:'';
last=(last) ? last[1]:'';
author=(author) ? author[1]:'';

java.put('tags',tags);
java.put('intro',intro);
java.put('last',last);
result=author;

